// MainHeader.js
import React, { useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // Импорт Link
import Login from './Login';
import Register from '../Regestrstion';
import logo from '../../Image/logo.jpg'; 
import basket from '../../Image/basket.svg'; 
import "./Header.css";

const MainHeader = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleLoginClose = () => setShowLogin(false);
  const handleRegisterClose = () => setShowRegister(false);

  return (
    <Navbar className="bg-white" expand="lg">
      <Container>
        {/* ... Остальной код ... */}
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/catalog">Каталог</Nav.Link>
            <Nav.Link as={Link} to="/about">О нас</Nav.Link>
            <Nav.Link as={Link} to="/account">Личный кабинет</Nav.Link>
          </Nav>
          <Nav>
            <Nav.Link onClick={() => setShowLogin(true)}>Вход</Nav.Link>
            <Nav.Link onClick={() => setShowRegister(true)}>Регистрация</Nav.Link>
            <Nav.Link as={Link} to="/cart">
              Корзина <img src={basket} alt="корзина" className="icon-placeholder" />
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>

      {/* Модальное окно для входа */}
      <Login show={showLogin} onHide={handleLoginClose} />

      {/* Модальное окно для регистрации */}
      <Register show={showRegister} onHide={handleRegisterClose} />
    </Navbar>
  );
};

export default MainHeader;
